import React from "react";
import axios from "axios";
import "./Comments.scss";
import UserPic from "../../Assets/Images/Mohan-muruge.jpg";
const DisplayComment = props => {
  const { comment, name, timestamp } = props.commentData;
  return (
    <article className="comments">
      <div className="comments__block">
        <div className="comments__pic"></div>
        <div className="comments__text">
          <div className="comments__top">
            <h4 className="comments__name">{name}</h4>
            <p className="comments__timestamp">{timestamp}</p>
          </div>
          <p className="comments__msg">{comment}</p>
        </div>
      </div>
    </article>
  );
};
// axios
// .get(
//   `https://project-2-api.herokuapp.com/videos/${this.state.mainVideo.id}?api_key=<f23538e5-0342-4d58-bbde-04e6a4ee4105>`
// )

export default class Comments extends React.Component {
  state = { comments: [] };

  newComment = e => {
    e.preventDefault();
    //Do Nothing for now
  };
  componentDidMount() {
    this.setState({
      comments: this.props.comments
    });
  }
  //   console.log("component has mounted");
  // }

  // componentDidUpdate(prevProps, prevState) {
  //   //   let currentID = this.props.comments;
  //   //   if (prevState.comments.id !== currentID) {
  //   //     this.setState({
  //   //       comments: currentID
  //   //     });
  //   axios
  //     .get(
  //       `https://project-2-api.herokuapp.com/videos/${this.props.id}/comments?api_key=<f23538e5-0342-4d58-bbde-04e6a4ee4105>`
  //     )
  //     .then(res => {
  //       // console.log(res.data);
  //       this.setState({
  //         comments: res.data
  //       });
  //     });
  //   //   }
  // }

  render() {
    return (
      <section>
        <h2 className="title">3 Comments</h2>
        <article className="form">
          <img src={UserPic} alt="" className="form__pic" />
          <form onSubmit={this.newComment}>
            <div className="form__tablet">
              <div className="form__text">
                <label htmlFor="comment">JOIN THE CONVERSATION</label>
                <textarea
                  name="comment"
                  className="form__comment"
                  placeholder="Write comment here"
                  required
                ></textarea>
              </div>
              <input type="submit" value="COMMENT" className="form__button" />
            </div>
          </form>
        </article>
        {console.log(this.props.id)}
        {this.state.comments.map(commentObj => {
          return (
            <div key={commentObj.id} className="comment__wrapper">
              <DisplayComment commentData={commentObj} />
            </div>
          );
        })}
      </section>
    );
  }
}
