import React from "react";
import "./App.css";
import Header from "./Components/Header/Header";
import Main from "./Components/Main/Main";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import axios from "axios";
import Upload from "./Components/Upload/Upload";
import VideoList from "./Components/VideoList/VideoList";
import FeaturedVid from "./Components/FeaturedVid/FeaturedVid";
export default class App extends React.Component {
  state = { videoList: [], isLoaded: false };
  componentDidMount() {
    console.log(this.props);
    axios
      .get(
        `https://project-2-api.herokuapp.com/videos/?api_key=<f23538e5-0342-4d58-bbde-04e6a4ee4105>`
      )
      .then(res => {
        this.setState({
          videoList: res.data,
          isLoaded: true
        });
      })
      .catch(error => console.error(error));
  }
  render() {
    if (this.state.isLoaded === true) {
      return (
        <>
          {console.log(this.props)}
          <BrowserRouter>
            <Header />
            <Switch>
              <Route
                path="/video/:id"
                render={() => <FeaturedVid videoID={this.state.videoList} />}
              />
              <Route
                exact
                path="/"
                render={() => <Main videoID={this.state.videoList} />}
              />
              <Route path="/upload" component={Upload} />
            </Switch>
          </BrowserRouter>
        </>
      );
    } else return <p>loading</p>;
  }
}
